bool isInSubsciptions(int tmpList[amountOfUniqueEvents], int possibleEntry) {
    int i = 0;
    for (i = 0; i < amountOfUniqueEvents; i++) {
        if (tmpList[i] == possibleEntry) {
            return true;
        } else if (tmpList[i] == -1) {
            return false;
        }
    }
    return false;
}

bool isIntInList(int tmpList[logSize], int possibleEntry) {
    int i = 0;
    for (i = 0; i < logSize; i++) {
        if (tmpList[i] == possibleEntry) {
            return true;
        } else if (tmpList[i] == 0) {
            return false;
        }
    }
    return false;
}


void addIntToList(int &tmpList[logSize], int newEntry) {
    int i = 0;
    for (i = 0; i < logSize; i++) {
        if (tmpList[i] == 0) {
            tmpList[i] = newEntry;
            return;
        }
    }
}


int getEventIDfromOrderCount(int orderCount) {
    int i = 0;
    for (i = 0; i < logSize; i++) {
        if (globalLog[i].orderCount == orderCount) {
            return globalLog[i].eventID;
        }
    }
    return -1;
}

void setNextLogToPropagate() {
    if (amountOfPropagation < amountOfLogs-2) {
        currentLogToPropagate = (currentLogToPropagate+1) % amountOfLogs;
        amountOfPropagation++;
    } else {
        // reset
        amountOfPropagation = -1;
    }
}


int getOrderCount() {
    int temp = eventOrderCounter;
    eventOrderCounter = eventOrderCounter + 1;
    return temp;
}


void setLogEntryForUpdate(int eventID, int emitterID, int basedOnOrderCount, bool ignored) {
    tempLogEntry.eventID = eventID;
    tempLogEntry.emitterID = emitterID;
    tempLogEntry.orderCount = getOrderCount();
    tempLogEntry.basedOnOrderCount = basedOnOrderCount;
    tempLogEntry.tiedTo = -1;
    tempLogEntry.ignored = ignored;
}


void findAndSetTiedTo(logEntryType &tempLog[logSize]) {
    int k;
    int j;
    int i;
    for(i = logSize-1; i >= 0; i--) {
        if (tempLog[i].orderCount != 0) {
            for (j = 0; j<maxAmountOfTied; j++) {
                if (tempLog[i].eventID == eventsTiedTo[tempLogEntry.eventID][j] && tempLog[i].tiedTo != -1) {
                    tempLogEntry.tiedTo = tempLog[i].orderCount;
                    return;
                }
            }
        }
    }
    for(i = logSize-1; i >= 0; i--) {
        if (tempLog[i].orderCount != 0) {
            if (tempLog[i].emitterID == tempLogEntry.emitterID) {
                for (j = 0; j<maxAmountOfTied; j++) {
                    for (k = 0; k<maxAmountOfTied; k++) {
                        if (eventsTiedTo[tempLogEntry.eventID][j] == eventsTiedTo[tempLog[i].eventID][k] && tempLog[i].tiedTo != -1) {
                            tempLogEntry.tiedTo = tempLog[i].tiedTo;
                            return;
                        }
                    }
                }
            }
        }
    }
    for(i = logSize-1; i >= 0; i--) {
        if (tempLog[i].orderCount != 0) {
            for (j = 0; j<maxAmountOfTied; j++) {
                if (tempLog[i].eventID == eventsTiedTo[tempLogEntry.eventID][j]) {
                    tempLogEntry.tiedTo = tempLog[i].orderCount;
                    return;
                }
            }
        }
    }  
}

void setPropagationLog(logEntryType tempLog[logSize]) {
    propagationLog = tempLog;
}


void updateGlobalLog() {
    int i;
    logEntryType resLog[logSize];
    int count = 0;
    bool addedEntry = false;

    for (i = 0; i < (logSize); i++) {
        if (globalLog[count].orderCount == 0) {
            if (addedEntry) {
                resLog[i] = globalLog[count];
                globalLog = resLog;
                return;
            }
            resLog[i] = tempLogEntry;
            globalLog = resLog;
            return;
        }
        else if (globalLog[count].orderCount > tempLogEntry.orderCount) {
            resLog[i] = tempLogEntry;
            count = count - 1;
            addedEntry = true;
        } else {
            resLog[i] = globalLog[count];
        }
        count = count + 1;
    }
    globalLog = resLog;
}

void updateLog(logEntryType &tempLog[logSize]) {
    int i = 0;
    findAndSetTiedTo(tempLog);
    updateGlobalLog();
    for (i = 0; i < logSize; i++) {
        if (tempLog[i].orderCount == 0) { // Log entry is unused since orderCount can never be 0
            tempLog[i] = tempLogEntry; // Update log
            return;
        }
    }
    return;
    // If we reach here log is full and we are in trouble
}


bool isInBranchingConflict(int partition, int eventID) {
    int i = 0;
    for (i = 0; i < innerSizeBranchingList; i++) {
        if (branchingList[partition][i] == eventID) {
            return true;
        }
    }
    return false;
}

bool handleBranchingEvent(logEntryType &tmpLogEntry,logEntryType &resLog[logSize], int &discardedEvents[logSize], int &discardedDueToCompetionEvents[logSize], int currentIndex) {
    int j;
    if (isIntInList(discardedDueToCompetionEvents, tmpLogEntry.basedOnOrderCount)) {
        tmpLogEntry.ignored = true;
    } 
    //Need to check if looping cause the we would accept it if is basedOn is in discardedEvents
    if (isIntInList(discardedEvents, tmpLogEntry.tiedTo)) {
        int tiedToEventID = getEventIDfromOrderCount(tmpLogEntry.tiedTo);
        if (isInBranchingPartion[tmpLogEntry.eventID] != isInBranchingPartion[tiedToEventID]) {
            tmpLogEntry.ignored = true;
        }
    }

    for (j = currentIndex; j >= 0; j--) {
        if (resLog[j].tiedTo == tmpLogEntry.tiedTo && tmpLogEntry.basedOnOrderCount == resLog[j].basedOnOrderCount) {
            if(isInBranchingConflict(isInBranchingPartion[tmpLogEntry.eventID], resLog[j].eventID)) {
                if (!isIntInList(discardedEvents, resLog[j].orderCount)) {
                    tmpLogEntry.ignored = true; //competetion
                    return true;
                }
            }
        }
    }
    return false;
}

bool handle_standard_setting(logEntryType &tmpLogEntry,logEntryType &resLog[logSize], int &discardedEvents[logSize], int &discardedDueToCompetionEvents[logSize], int currentIndex) {
    int i;
    if (isBranchingList[tmpLogEntry.eventID]) {
        return handleBranchingEvent(tmpLogEntry, resLog, discardedEvents, discardedDueToCompetionEvents, currentIndex);
    }
    if (isIntInList(discardedDueToCompetionEvents, tmpLogEntry.basedOnOrderCount)) {
        tmpLogEntry.ignored = true;
        return false;
    }
    for (i = currentIndex; i >=0; i--) {
        if (resLog[i].eventID == tmpLogEntry.eventID && resLog[i].tiedTo == tmpLogEntry.tiedTo) {
            tmpLogEntry.ignored = true;
            return false;
        }
    }
    return false;
}

bool handleStandardEvent(logEntryType &tmpLogEntry,logEntryType &resLog[logSize], int &discardedEvents[logSize], int &discardedDueToCompetionEvents[logSize], int currentIndex) {
    int i;
    if (standardSetting) {
        return handle_standard_setting(tmpLogEntry, resLog, discardedEvents, discardedDueToCompetionEvents, currentIndex);
    }
    if (isBranchingList[tmpLogEntry.eventID]) {
        return handleBranchingEvent(tmpLogEntry, resLog, discardedEvents, discardedDueToCompetionEvents, currentIndex);
    }
    if (isIntInList(discardedDueToCompetionEvents, tmpLogEntry.basedOnOrderCount) || isIntInList(discardedEvents, tmpLogEntry.basedOnOrderCount)) {
        tmpLogEntry.ignored = true;
        return false;
    }
    for (i = currentIndex; i >= 0; i--) {
        if (resLog[i].eventID == tmpLogEntry.eventID && resLog[i].tiedTo == tmpLogEntry.tiedTo) {
            tmpLogEntry.ignored = true; //competetion
            return true;
        }
    }
    return false;
}

bool handleOwnEvent(logEntryType &tmpLogEntry,logEntryType &resLog[logSize], int &discardedEvents[logSize], int &discardedDueToCompetionEvents[logSize], int currentIndex, int id, bool &olderEntryIgnored) {
    int j;
    bool inCompetetion = false;
    if (standardSetting) {
        return handle_standard_setting(tmpLogEntry, resLog, discardedEvents, discardedDueToCompetionEvents, currentIndex);
    }
    if (tmpLogEntry.emitterID == id && isIntInList(discardedEvents, tmpLogEntry.basedOnOrderCount)) {
        tmpLogEntry.ignored = true; //competetion
        return true;
    }
    inCompetetion = handleStandardEvent(tmpLogEntry,resLog, discardedEvents, discardedDueToCompetionEvents, currentIndex);
    if (tmpLogEntry.emitterID != id) {
        tmpLogEntry.ignored = true;
        // Check if we have already tied into this so an event we emitted that is tied to the same thing
        for (j = currentIndex; j >= 0; j--) {
            if (resLog[j].emitterID == id && resLog[j].eventID == tmpLogEntry.eventID && resLog[j].tiedTo == tmpLogEntry.tiedTo) {
                olderEntryIgnored = false;
                return inCompetetion;
                }
        }
        for (j = currentIndex; j >= 0; j--) {
            if (resLog[j].tiedTo != -1 && resLog[j].tiedTo == tmpLogEntry.tiedTo || tmpLogEntry.tiedTo == resLog[j].orderCount) {
                resLog[j].ignored = true;
                olderEntryIgnored = true;
            }
        }
    }
    return inCompetetion;
}


void updateLogDoor(logEntryType &tempLog[logSize], int &emittedOrderCounts[logSize], int log_id_start) {    
    addIntToList(emittedOrderCounts, tempLogEntry.orderCount);
    // We check if basedemitterID set to -2 as this is a flag for unknown
    tempLogEntry.emitterID = tempLogEntry.emitterID + log_id_start;
    if (tempLogEntry.basedOnOrderCount == -2) {
        int i = 0;
        for (i = logSize-1; i >= 0; i--) {
            if (tempLog[i].orderCount == 0) {
            } else { 
                if (tempLogEntry.eventID == Close_ID) {
                    if (tempLog[i].eventID == Open_ID || tempLog[i].eventID == Deliver_ID) {
                        tempLogEntry.basedOnOrderCount = tempLog[i].orderCount;
                        updateLog(tempLog);
                        return;
                    }
                }
            }
        }
    }
    tempLogEntry.basedOnOrderCount = -1;
    updateLog(tempLog);
    return;
}

void updateLogForklift(logEntryType &tempLog[logSize], int &emittedOrderCounts[logSize], int log_id_start) {    
    addIntToList(emittedOrderCounts, tempLogEntry.orderCount);
    // We check if basedemitterID set to -2 as this is a flag for unknown
    tempLogEntry.emitterID = tempLogEntry.emitterID + log_id_start;
    if (tempLogEntry.basedOnOrderCount == -2) {
        int i = 0;
        for (i = logSize-1; i >= 0; i--) {
            if (tempLog[i].orderCount == 0) {
            } else { 
                if (tempLogEntry.eventID == Get_ID) {
                    if (tempLog[i].eventID == Request_ID) {
                        tempLogEntry.basedOnOrderCount = tempLog[i].orderCount;
                        updateLog(tempLog);
                        return;
                    }
                }
            }
        }
    }
    tempLogEntry.basedOnOrderCount = -1;
    updateLog(tempLog);
    return;
}

void updateLogTransport(logEntryType &tempLog[logSize], int &emittedOrderCounts[logSize], int log_id_start) {    
    addIntToList(emittedOrderCounts, tempLogEntry.orderCount);
    // We check if basedemitterID set to -2 as this is a flag for unknown
    tempLogEntry.emitterID = tempLogEntry.emitterID + log_id_start;
    if (tempLogEntry.basedOnOrderCount == -2) {
        int i = 0;
        for (i = logSize-1; i >= 0; i--) {
            if (tempLog[i].orderCount == 0) {
            } else { 
                if (tempLogEntry.eventID == Request_ID) {
                    if (tempLog[i].eventID == Deliver_ID || tempLog[i].eventID == Open_ID) {
                        tempLogEntry.basedOnOrderCount = tempLog[i].orderCount;
                        updateLog(tempLog);
                        return;
                    }
                } else if (tempLogEntry.eventID == Deliver_ID) {
                    if (tempLog[i].eventID == Get_ID) {
                        tempLogEntry.basedOnOrderCount = tempLog[i].orderCount;
                        updateLog(tempLog);
                        return;
                    }
                }
            }
        }
    }
    tempLogEntry.basedOnOrderCount = -1;
    updateLog(tempLog);
    return;
}

